const imgUrl = {
  encourage: 'https://s21.lgstatic.com/growth/activity/20210304/1614843924038.png',
  exposure: 'https://s21.lgstatic.com/growth/activity/20210407/1617786481368.png',
  position: 'https://s21.lgstatic.com/growth/activity/20210407/1617787121457.png'
}
const mySwiper = that.$refs.swiperRef.swiper
const luckToastDom = document.querySelector('.luck-toast')
if (state == 1 && content) {
  let index = 2
  switch (content.type) {
    case 'encourage':
      index = 2
      encourageInit()
      break;
    case 'exposure':
      index = 3
      exposureInit()
      break;
    case 'position':
      index = 4
      positionInit()
      break;
  }
  updateResult()
  mySwiper.slideTo(index, 0, false)
  function updateResult() {
    let resultNode = that.node.children[1].children.find((item) => item.name === \

)
    let { num } = content
    if (num) {
      that._updateComponent({value: `您今日抽签还剩${3-num}次`}, that.nodeTree, that.selectNode, 'JaXfFQw7')
    }
    that._updateComponent({insideStyle: {backgroundImage: imgUrl[content.type]}}, that.nodeTree, resultNode)
  }
  function encourageInit() {
    updateResult()
  }
  function exposureInit() {
    updateResult()
  }
  function positionInit() {
      updateResult()
      let positionNode = that.node.children[index].children.find((item) => item.name === \

)
      let children = [...positionNode.children]
      children.forEach((item, itemIndex) => {
          item.positionInfo = content.positions[itemIndex].positionCardVo
      })
      that._updateComponent({children}, that.nodeTree, positionNode)
  }
} else {
  luckToastDom.style.display = 'flex'
}

