const that = this
const wrapDom = document.querySelector('.record-card-container')
const pageNode = that.findParentComponentById(that.nodeTree, that.node.id);
const shareModelNode = pageNode.children.find(item => item.type === 'share')
const imgSrcArr = {
  encourage: 'https://s21.lgstatic.com/growth/activity/20210309/1615256837460.png',
  exposure: 'https://s21.lgstatic.com/growth/activity/20210309/1615256877449.png',
  position: 'https://s21.lgstatic.com/growth/activity/20210309/1615256889763.png'
}
let timer = setTimeout(() => {
  that.$nextTick(() => {
    addShare()
  })
}, 3000)
wrapDom.addEventListener('scroll', () => {
  clearInterval(timer)
  timer = setTimeout(() => {
    that.$nextTick(() => {
      addShare()
    })
  }, 3000)
})
wrapDom.addEventListener('touchstart', (e) => {
  e.stopPropagation()
})
wrapDom.addEventListener('touchend', (e) => {
  e.stopPropagation()
})
function addShare() {
  const shareDoms = document.querySelectorAll('.record-card-container .record-container')
  const shareClickDoms = Array.from(shareDoms)
  const shareNodeArr = that.node.children
  for (let index = 0; index < shareClickDoms.length; index++) {
    const element = shareClickDoms[index];
    const shareNode = shareNodeArr[index]
    element.removeEventListener('click', () => { })
    element.addEventListener('click', () => {
      let { info } = shareNode
      let shareConfig = {
        title: '我把求职好运转给你啦',
        desc: info.shareDescription,
        thumb: imgSrcArr[info.type],
        url: window.location.href
      }
      if (!window.lgBridge) {
        that.LgShare.init({
          title: shareConfig.title,
          desc: shareConfig.desc,
          shareUrl: shareConfig.url,
          imgUrl: shareConfig.thumb,
          success: (res) => {}
        });
        that._updateComponent({
          style: { display: 'block' }
        }, that.nodeTree, that.selectNode, \

);
      } else {
        that._updateComponent({
          shareConfig,
          style: { display: 'block' }
        }, that.nodeTree, that.selectNode, shareModelNode.id);
      }
    })
  }
}

