  * 1.动作执行函数doAction，可以执行所有类型的动作
  * 2.通过上下文对象context可以获取当前组件实例，例如context.props可以获取该组件相关属性
  * 3.事件对象event，在doAction之后执行event.stopPropagation();可以阻止后续动作执行
*/
const {
  hasValidate, 
  order, 
  kind, 
  roleType, 
  user, 
  amount,
  type = 605,
} = context.props.data
let valid = true
const billTitle = +type === 605 ? '扣款' : '补款'
if (!order || hasValidate !== 2) {
  myMsg = '请输入并验证订单';
  valid = false
} else if (!roleType) {
  myMsg = '请选择角色类型';
  valid = false
} else if (!user) {
  myMsg = '请选择人员';
  valid = false
} else if (!kind) {
  myMsg = `请选择${billTitle}类型`;
  valid = false
} else if (!+amount) {
  myMsg = `请选择${billTitle}金额`;
  valid = false
}

if (!valid) {
  doAction({
    actionType: 'toast',
    args: {
      msg: myMsg
    }
  });
  return event.stopPropagation()
}

